require("dotenv").config(); // Make sure to load environment variables at the top
import "./App.css";
import Layout from "./components/Layout/Layout";
const express = require("express");
const app = express();
const cors = require("cors"); // Correct the package name to 'cors'
const PORT = 6005;

app.use(cors({
  origin: "http://localhost:3000",
  methods: "GET,POST,PUT,DELETE",
  credentials: true
}));

app.use(express.json());

app.get("/", (req, res) => {
  res.status(200).json("Server started");
});

app.listen(PORT, () => {
  console.log(`Server started at port no ${PORT}`); // Use backticks for dynamic variable interpolation
});

function App() {
  return <Layout />;
}

export default App;
