import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { Card, CardContent, Typography, Box, Grid, Button, Drawer, List, ListItem, ListItemText, Divider } from '@mui/material';
import axios from 'axios';

const FilteredOptionsCard = () => {
  // States to store fetched data for accommodations and transportations
  const [accommodations, setAccommodations] = useState([]);
  const [transportations, setTransportations] = useState([]);

  // Getting the state passed through React Router's useLocation hook
  const { state } = useLocation();
  const { source, destination, date, budget } = state || {}; // Ensure state is not null to avoid errors
  const navigate = useNavigate();
  
  // States to track the selected transportation and accommodation options
  const [selectedTransportation, setSelectedTransportation] = useState(null);
  const [selectedAccommodation, setSelectedAccommodation] = useState(null);

  // Calculate the total cost based on selected options
  const totalCost = (selectedTransportation?.price || 0) + (selectedAccommodation?.pricePerNight || 0);

  // Function to fetch data from the API
  const getData = async () => {
    try {
      // Fetching transportations data
      const travelResponse = await axios.get('/api/traveling/');
      setTransportations(travelResponse.data);

      // Fetching accommodations data
      const hotelResponse = await axios.get('/api/hotel/');
      setAccommodations(hotelResponse.data);
    } catch (error) {
      console.error('Failed to fetch data:', error);
    }
  };

  // Fetch data when the component mounts
  useEffect(() => {
    getData();
  }, []);

  // Filter transportation options based on the source and destination
  const filteredTransportations = transportations.filter(
    (trans) =>
      trans.departure.location.toLowerCase().includes(source?.toLowerCase()) &&
      trans.arrival.location.toLowerCase().includes(destination?.toLowerCase())
  );

  // Filter accommodation options based on the destination
  const filteredAccommodations = accommodations.filter((acc) =>
    acc.address.city.toLowerCase().includes(destination?.toLowerCase())
  );

  return (
    <Box sx={{ display: 'flex', flexGrow: 1, padding: 2 }}>
      {/* Main content section */}
      <Box sx={{ flexGrow: 1 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Available Transportations
        </Typography>
        <Grid container spacing={3}>
          {/* Rendering filtered transportation options */}
          {filteredTransportations.length > 0 ? (
            filteredTransportations
              .filter((item) => (budget === null || item.price <= budget)) // Filter based on budget
              .map((transportation, index) => (
                <Grid item xs={12} sm={6} md={4} key={index}>
                  <Card sx={{ maxWidth: 345, bgcolor: 'lightgreen', borderRadius: 2 }}>
                    <CardContent>
                      <Typography gutterBottom variant="h5" component="div" sx={{ color: 'darkgreen' }}>
                        {transportation.type} - {transportation.provider}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        Departure: {transportation.departure.location}
                        <br />
                        Date: {transportation.departure.dateTime}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        Arrival: {transportation.arrival.location}
                        <br />
                        Date: {transportation.arrival.dateTime}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        Price: ₹{transportation.price}
                      </Typography>
                      <Button 
                        variant="contained" 
                        color="primary" 
                        onClick={() => setSelectedTransportation(transportation)}
                        sx={{ mt: 2 }}
                      >
                        Select Transportation
                      </Button>
                    </CardContent>
                  </Card>
                </Grid>
              ))
          ) : (
            // Display message if no transportation options are found
            <Typography variant="h6" component="div" sx={{ mt: 2 }}>
              No transportations available for the selected route and date.
            </Typography>
          )}
        </Grid>

        <Typography variant="h4" component="h1" gutterBottom sx={{ mt: 4 }}>
          Available Accommodations
        </Typography>
        <Grid container spacing={3}>
          {/* Rendering filtered accommodation options */}
          {filteredAccommodations.length > 0 ? (
            filteredAccommodations
              .filter((item) => (budget === null || item.pricePerNight <= budget)) // Filter based on budget
              .map((accommodation, index) => (
                <Grid item xs={12} sm={6} md={4} key={index}>
                  <Card sx={{ maxWidth: 345, bgcolor: 'lightblue', borderRadius: 2 }}>
                    <CardContent>
                      <Typography gutterBottom variant="h5" component="div" sx={{ color: 'darkblue' }}>
                        {accommodation.name}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        Price per Night: ₹{accommodation.pricePerNight}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        Address: {accommodation.address.street}, {accommodation.address.city}, {accommodation.address.state}
                      </Typography>
                      <Button 
                        variant="contained" 
                        color="primary" 
                        onClick={() => setSelectedAccommodation(accommodation)}
                        sx={{ mt: 2 }}
                      >
                        Select Accommodation
                      </Button>
                    </CardContent>
                  </Card>
                </Grid>
              ))
          ) : (
            // Display message if no accommodation options are found
            <Typography variant="h6" component="div" sx={{ mt: 2 }}>
              No accommodations available at the destination.
            </Typography>
          )}
        </Grid>
      </Box>

      {/* Sidebar for showing selected options */}
      <Drawer
        anchor="right"
        variant="permanent"
        sx={{
          width: 300,
          flexShrink: 0,
          [`& .MuiDrawer-paper`]: { width: 300, boxSizing: 'border-box' },
        }}
      >
        <Typography variant="h6" component="div" sx={{ p: 2 }}>
          Selected Options
        </Typography>
        <Divider />
        <List>
          {/* Show selected transportation details */}
          {selectedTransportation && (
            <ListItem>
              <ListItemText 
                primary={`${selectedTransportation.type} - ${selectedTransportation.provider}`}
                secondary={`Price: ₹${selectedTransportation.price}`}
              />
            </ListItem>
          )}
          {/* Show selected accommodation details */}
          {selectedAccommodation && (
            <ListItem>
              <ListItemText 
                primary={selectedAccommodation.name}
                secondary={`Price per Night: ₹${selectedAccommodation.pricePerNight}`}
              />
            </ListItem>
          )}
          <Divider />
          {/* Show total cost */}
          <ListItem>
            <ListItemText 
              primary="Total Cost"
              secondary={`₹${totalCost}`}
            />
          </ListItem>
        </List>
        <Box sx={{ p: 2 }}>
          {/* Button to proceed to payment, navigates to the payment page */}
          <Button 
            variant="contained" 
            color="primary" 
            fullWidth
            onClick={() => navigate('/makepayment', { state: { totalCost, selectedAccommodation, selectedTransportation } })}
          >
            Proceed to Payment
          </Button>
        </Box>
      </Drawer>
    </Box>
  );
};

export default FilteredOptionsCard;
