import React from 'react';
import '../Styling/city.css'
import { Container, Row, Col, Card, CardBody, CardTitle, CardText, CardImg } from 'reactstrap';

const cities = [
  {
    name: 'Mumbai',
    description: 'The financial capital of India, known for Bollywood and the Gateway of India.',
    image: '../images/Mumbai.jpeg',
  },
  {
    name: 'Delhi',
    description: 'The capital city of India, rich in history and home to several iconic landmarks.',
    image: '/path-to-your-image/delhi.jpg',
  },
  {
    name: 'Bangalore',
    description: 'The Silicon Valley of India, known for its IT industry and pleasant climate.',
    image: '/path-to-your-image/bangalore.jpg',
  },
  {
    name: 'Chennai',
    description: 'The cultural hub of South India, known for its temples and classical music.',
    image: '/path-to-your-image/chennai.jpg',
  },
  {
    name: 'Kolkata',
    description: 'The cultural capital of India, known for its art, literature, and colonial architecture.',
    image: '/path-to-your-image/kolkata.jpg',
  },
  {
    name: 'Hyderabad',
    description: 'The city of pearls, known for its history, culture, and cuisine.',
    image: '/path-to-your-image/hyderabad.jpg',
  },
  {
    name: 'Pune',
    description: 'A vibrant city known for its educational institutions and cultural heritage.',
    image: '/path-to-your-image/pune.jpg',
  },
  {
    name: 'Ahmedabad',
    description: 'The largest city in Gujarat, known for its textile industry and historic sites.',
    image: '/path-to-your-image/ahmedabad.jpg',
  },
];

const CityPage = () => {
  return (
    <Container className="my-5">
      <Row>
        {cities.map((city, index) => (
          <Col lg="4" md="6" sm="12" className="mb-4" key={index}>
            <Card>
              <CardImg top width="100%" src={city.image} alt={city.name} />
              <CardBody>
                <CardTitle tag="h5">{city.name}</CardTitle>
                <CardText>{city.description}</CardText>
              </CardBody>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default CityPage;
